const latexAstParser = require('latex-ast-parser');

// ~~~

let count = 0;
/**
 * @type {string[]}
 */
let buffer = [];
/**
 * @param {{ type: string, content: any }[]} node
 * @returns {void}
 */
const latexNodeVisitor = (node) => {
  node.forEach((n) => {
    if (Array.isArray(n.content) && n.type === 'environment') {
      latexNodeVisitor(n.content);
    } else {
      if (new RegExp(/^\\d*$/).test(n.content) && n.type === 'string') {
        if (count > 1) buffer.push(popSidParFour(n.content));
        count++;
      }
      if (
        n.type === 'group' &&
        n.content[0].type !== 'comment' &&
        n.content[0].content !== 'jarticle'
      ) {
        buffer.push(n.content[0].content);
      }
    }
  });
};

/**
 *
 * @param {(arg: { type: string, content: any }[]) => void} visitor
 * @returns
 */
const latexNodeTraverser = (visitor) => {
  return (ast) => {
    if (ast.type === 'root') {
      return visitor(ast.content);
    } else {
      throw new Error("Given AST's type is not root.");
    }
  };
};

// ~~~

(async () => {
  // ~~~
  const textRubyTexData = await getTextRubyData(
    'LONG',
    TEXT_DATA_CONFIGS.DEBUG_FILE_NUMBER
  );

  const traverse = latexNodeTraverser(latexNodeVisitor);
  traverse(latexAstParser.parse(textRubyTexData));
  // ~~~
})();

