export const getStaticProps = async () => {
  // Contentful から記事のデータを取得
  const entries = await client.getEntries<Slug>();

  if (entries != null) {
    // revalidate を設定
    return { props: { entries }, revalidate: 1 };
  } else throw new Error();
};

